function openRandomPopup() {
    var randomIndex = Math.floor(Math.random() * fileUrls.length);
    var randomFileUrl = fileUrls[randomIndex];
    
    var width = 600;
    var height = 400;

    var screenWidth = screen.width;
    var screenHeight = screen.height;

    var randomLeft = Math.floor(Math.random() * (screenWidth - width));
    var randomTop = Math.floor(Math.random() * (screenHeight - height));

    var popupWindow = window.open(randomFileUrl, '_blank', 'width=' + width + ', height=' + height + ', left=' + randomLeft + ', top=' + randomTop);
}

